%% Fit: 'untitled fit 1'.
[xData, yData] = prepareCurveData( circle_x, circle_y );

% Set up fittype and options.
ft = fittype( 'poly2' );

% Fit model to data.
[fitresult, ~] = fit( xData, yData, ft );
coeff = coeffvalues(fitresult);
